mapper.select:
SELECT * FROM (SELECT M.*,ROWNUM RN FROM ( SELECT M.MENU_CODE AS MENUCODE, M.MENU_NAME AS MENUNAME, M.MENU_DESC AS MENUDESC, M.MENU_URL AS MENUURL, M.UP_MENU_CODE AS UPMENUCODE, M.MENU_LEVEL AS MENULEVEL, M.STATUS, U.MENU_NAME UPMENUNAME FROM TB_SYS_MENU M LEFT JOIN TB_SYS_MENU U ON M.UP_MENU_CODE = U.MENU_CODE WHERE M.STATUS= '1'
AND M.MENU_NAME LIKE '%${menuName}%'
ORDER BY M.MENU_CODE DESC ) M) N where n.rn > ${start} and rn < = ${end}

SELECT COUNT(1) FROM TB_SYS_MENU M WHERE M.STATUS= '1'
AND M.MENU_NAME LIKE '%${menuName}%'
AND M.MENU_CODE != #{menuCode}

mapper.update:
UPDATE TB_SYS_MENU
SET MENU_CODE = #{menuCode}

	,MENU_NAME = #{menuName,jdbcType=VARCHAR}


	,MENU_DESC = #{menuDesc,jdbcType=VARCHAR}


	,MENU_URL = #{menuUrl,jdbcType=VARCHAR}


	,UP_MENU_CODE = #{upMenuCode,jdbcType=VARCHAR}


	,UP_MENU_CODE = #{upMenuCode,jdbcType=VARCHAR}


	,MENU_LEVEL = #{menuLevel,jdbcType=VARCHAR}


	,STATUS = #{status}

WHERE MENU_CODE = #{menuCode}

mapper.insert:
BEGIN
INSERT INTO TB_SYS_ROLE_MENU (ROLEID, MENU_CODE) VALUES ( #{item.roleId,jdbcType=VARCHAR}, #{item.menuCode,jdbcType=VARCHAR})
;END ;
