mapper.select:
SELECT nvl(count(1),0) FROM TB_SYS_ROLE R WHERE 1=1
AND R.ROLENAME LIKE '%${roleName}%'
AND R.ROLEID != #{roleId}

SELECT * FROM (SELECT M.*,ROWNUM RN FROM (SELECT R.ROLEID, R.ROLENAME FROM TB_SYS_ROLE R WHERE 1=1
AND R.ROLENAME LIKE '%${roleName}%'
ORDER BY R.ROLEID) M) N where n.rn > ${start} and rn < = ${end}

mapper.update:
UPDATE TB_SYS_ROLE
SET
ROLENAME = #{roleName}
WHERE ROLEID = #{roleId}

mapper.select:
SELECT COUNT(1) EXISTS_SUM FROM TB_SYS_USER_ROLE
WHERE
ROLEID = #{roleId}
